import React, { useState, useEffect } from 'react'
import { generate } from 'random-words'
import { toBeChecked } from '@testing-library/jest-dom/matchers'

export default function Typing({ numWords }) {
    const [words, setWords] = useState([])
    const [currInput, setCurrInput] = useState([])
    const [char, setChar] = useState("")
    const [currWord, setCurrWord] = useState(0)
    const [currIndex, setCurrIndex] = useState(-1)
    const [completedLines, setCompletedLines] = useState(0);
    const [currLineIndex, setCurrLineIndex] = useState(0);


    useEffect(() => {
        setWords(generateWords())
    }, [numWords])

    function generateWords(n) {
        return new Array(numWords).fill(null).map(() => generate())
    }

    let keyCode = 0;

    const handleKeyDown = (event) => {
        keyCode = event.keyCode;
        if (keyCode === 32) {
          setCurrInput([]);
          setCurrWord((prevState) => prevState + 1);
          setCurrIndex(-1);
          setChar('space');
          if ((currWord + 1) % 7 === 0) {
            setCompletedLines((prevState) => prevState + 1);
          }
        } else if (keyCode === 8) {
          if (currInput.length !== 0) {
            setCurrInput((currInput) => currInput.slice(0, -1));
            setCurrIndex((index) => {
              if (index >= 0) {
                return index - 1;
              } else {
                return -1;
              }
            });
            setChar('backspace');
          }
        } else if (keyCode >= 65 && keyCode <= 90) {
          let chrCode = keyCode - 48 * Math.floor(keyCode / 48);
          let chr = String.fromCharCode(96 <= keyCode ? chrCode : keyCode);
          setChar(chr.toLowerCase());
          setCurrInput((Input) => [...Input, chr.toLowerCase()]);
          setCurrIndex((Index) => Index + 1);
        }
      };


    function injectExtraWords(words, currIndex, currWord, currInput, char) {
        let space = document.createElement("span")
        space.appendChild(
            document.createTextNode(" "))
        if (char !== "backspace" && currIndex >= words[currWord]?.length) {


            let element = document.createElement("span");
            element.key = currIndex + 1
            element.id = `${currWord}-${currIndex}`
            element.className = "word-incorrect"
            element.onKeyDown = "handleKeyDown"

            element.appendChild(
                document.createTextNode(`${currInput[currInput.length - 1]}`)
            )
            const currWordElement = document.getElementById(currWord);
            if (currWordElement) {
                currWordElement.removeChild(currWordElement.lastChild);
                currWordElement.appendChild(element);
                currWordElement.appendChild(space);
            }
            document.getElementById(currWord).removeChild(document.getElementById(currWord).lastChild)
            document.getElementById(currWord).appendChild(element)
            document.getElementById(currWord).appendChild(space)



        }
        else if (char === "backspace" && currIndex >= words[currWord]?.length - 1) {
            const currWordElement = document.getElementById(currWord);
            if (currWordElement) {
                currWordElement.removeChild(currWordElement.lastChild);
                currWordElement.removeChild(currWordElement.lastChild);
                currWordElement.appendChild(space);
            }
            document.getElementById(currWord).removeChild(document.getElementById(currWord).lastChild)
            document.getElementById(currWord).removeChild(document.getElementById(currWord).lastChild)
            document.getElementById(currWord).appendChild(space)

        }

    }



    function changeColor() {

        let element = document.getElementById(`${currWord}-${currIndex}`)
        console.log(currWord, currIndex)
        // console.log(element.innerHTML)
        if (char === "backspace") {
            let element2 = document.getElementById(`${currWord}-${currIndex + 1}`)
            if (element2) {
                element2.className = "word-unrendered"
            }
        }
        else {
            if (element) {
                element.className = `word-${checkMatch(char)}`

            }
        }

    }

    useEffect(() => {
        if (currWord >= 60 && (currWord - completedLines * 7) % 7 === 0) {
          setCompletedLines((lines) => lines + 1);
        }
      }, [currWord]);

    useEffect(() => {

        injectExtraWords(words, currIndex, currWord, currInput, char)


    }, [currInput])

    useEffect(() => {
        changeColor()
    }, [currInput])


    useEffect(() => {
        window.addEventListener('keydown', handleKeyDown);

        return () => {
            window.removeEventListener('keydown', handleKeyDown);
        };
    }, []);


    useEffect(() => {
        console.log('A key was pressed: ', currInput, char, words[currWord], currIndex);
    }, [currInput]);


    function checkMatch(char) {
        if (currIndex >= words[currWord].length) {
            return "redundant"
        }

        if (words[currWord][currIndex] === char) {
            return "correct"
        } else {
            return "incorrect"
        }
    }

    const maxLines = Math.ceil(words.length / 7); // Calculate the maximum number of lines
    const startLineIndex = completedLines * 7; // Calculate the starting line index
    const endLineIndex = Math.min((completedLines + 1) * 7, words.length); // Calculate the ending line index

  return (
    <div>
      <div className="typing--section">
        <div className="prompt">
          {words.slice(startLineIndex, endLineIndex).map((word, i) => {
            const lineIndex = startLineIndex + i;
            const isCurrentLine = lineIndex === currLineIndex;
            return (
              <span key={i} id={`${i}`}>
                {word.split('').map((char, idx) => (
                  <span
                    key={idx}
                    id={`${i}-${idx}`}
                    className={`word-${isCurrentLine ? checkMatch(char) : 'unrendered'}`}
                  >
                    {char}
                  </span>
                ))}
                <span> </span>
              </span>
            );
          })}
        </div>
      </div>
    </div>
  );
}
